mapper.select:
SELECT DISTINCT m.*
  FROM user u,user_role ur,menu_role mr,menu m
  where u.id = ur.user_id and m.id = mr.menu_id and ur.role_id = mr.role_id and u.username = #{userName}

SELECT DISTINCT m.*
  FROM user u,user_role ur,menu_role mr,menu m
  where u.id = ur.user_id and m.id = mr.menu_id and ur.role_id = mr.role_id and u.id = #{userId}

select
  id, pid, url, resources, title, icon, type, remarks, gmt_create, gmt_modified
from times.menu


        and id = #{id}


        and pid = #{pid}


        and url = #{url}


        and resources = #{resources}


        and title = #{title}


        and icon = #{icon}


        and type = #{type}


        and remarks = #{remarks}


        and gmt_create = #{gmtCreate}


        and gmt_modified = #{gmtModified}
